package Maths;

public class math {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		double x = 3.148787;
		double y = -13;
		
		double z = Math.min(x, y); //minimum of two number
		
		double g = Math.abs(y); //absolute value of two numbers
		double h = Math.sqrt(x); //square root
		double c =Math.round(x); //roundabout value
	
		System.out.println(z);
		System.out.println(g);	
		System.out.println(h);
		System.out.println(c);
	}
	
	

}
