package SwitchCase;

public class Switch {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		String day="Monday";
		
		switch(day) {
		
		case "Sunday": System.out.println("ITS SUNDAY!!!!!!!!");
		break;
		case "Monday": System.out.println("ITS MONDAYYYYYYYYYYYYY!!!!!!!!");
		break;
		case "Tuesday": System.out.println("ITS TuesdayY!!!!!!!!");
		break;
		case "Wednesday": System.out.println("ITS Wednsesday!!!!!!!!");
		break;
		case "Friday": System.out.println("ITS Friday!!!!!!!!");
		break;
		case "Saturday": System.out.println("ITS SATURDAY!!!!!!!!");
		break;
		case "Thursday": System.out.println("ITS Thursday!!!!!!!!");
		break;
		default : System.out.println("NOT A CORRECT DAy");
		}
	}

}
